import axios from 'axios';
import { searchContext } from '../components/Contexts';
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import search from '../icons/search.svg'



function SearchMovie() {

    const [foundMovies, setFoundMovies] = useState([])
    const [query, setQuery] = useState("")
    const [url, setUrl] = useState("")


    useEffect(() => {
        const fecthMovies = async () => {
            const fecthFoundMovies = await axios(url)
            setFoundMovies(fecthFoundMovies.data.results)
        }
        fecthMovies()
    }, [url])


    return (
        <searchContext.provider value={foundMovies}>
            <div>
                <input type="text" placeholder="Search movie" onChange={event => setQuery(event.target.value)} />
                <button>
                    <Link to="/blog"><img src={search} alt="search" onClick={() =>
                        setUrl(`https://api.themoviedb.org/3/search/movie?api_key=e2a2f53fe94c336a47e632ddb6b9fc26&language=en-US&query=${query}&page=all&include_adult=false`)
                    } /></Link>
                </button>
            </div>
        </searchContext.provider>
    )
}

export default SearchMovie
