import React from 'react';
import Wrapper from '../components/Wrapper';
import styles from '../style/movies.module.scss';
import bg from "../img/bg.jpg";
import bg2 from "../img/bg2.jpg";
import bg3 from "../img/bg3.jpg";
import heart from '../icons/heart.svg';
import CardMovie from '../components/CardMovie';
import Footer from '../components/Footer';


export default function ActionMovies({ getMoviesByCategories, MoviesByGenre, genres }) {
    const getGenresMovies = (genreId) => {
        getMoviesByCategories(genreId)
    }

    let sorted = MoviesByGenre.sort((a, b) => {
        return b.vote_count - a.vote_count
    })
    console.log(sorted.slice(0, 3));
    return (
        <div className={styles.body}>
            <div className={styles.parent__presentaion_cards}>



                <div>
                    <img src={bg} alt="bg" className="w-100 rounded-2" />
                    <div>
                        <p>{sorted ? sorted[0].title : "avatar"}</p>
                        <p>{sorted ? sorted[0].overview : "Lorem ipsum"}</p>
                        <span> <img src={heart} alt="" /></span>
                    </div>
                </div>
                <div>
                    <img src={bg2} alt="bg" className="w-100 rounded-2" />
                    <div>
                        <p>Avatar</p>
                        <p>Lorem ipsum, dolor sit amet consectetur adipisicing elit. Corrupti, optio.</p>
                        <span> <img src={heart} alt="" /></span>
                    </div>
                </div>
                <div>
                    <img src={bg3} alt="bg" className="w-100 rounded-2" />
                    <div>
                        <p>SPIDERMAN</p>
                        <p>Lorem ipsum, dolor sit amet consectetur adipisicing elit. Corrupti, optio.</p>
                        <span> <img src={heart} alt="" /></span>

                    </div>
                </div>
                <ul>
                    {
                        genres.map(genre => <li key={genre.id} className="m-1"> <button onClick={() => getGenresMovies(genre.id)}> <nobr>{genre.name}</nobr></button></li>)

                    }

                </ul>
            </div>
            <h2> <span>A</span>ction Movies</h2>
            <div className={styles.Movies__cards__parent}>
                {
                    MoviesByGenre.map(Movie => {
                        return <CardMovie Movie={Movie} />
                    })
                }
            </div>
            <Footer />
        </div>
    )
}
