import styles from '../style/Navbar.module.scss';
import Sidebar from '../components/sidebar';
import SearchMovie from '../components/SearchMovie';


export default function Navigation() {

    const [foundMovies, setFoundMovies] = useState([])
    const [query, setQuery] = useState("")
    const [url, setUrl] = useState("")


    useEffect(() => {
        const fecthMovies = async () => {
            const fecthFoundMovies = await axios(url)
            setFoundMovies(fecthFoundMovies)
        }
        fecthMovies()
    }, [url])

    return (
        <div>
            <div className={styles.navigation}>
                 <searchContext.provider value={foundMovies}>
            <div>
                <input type="text" placeholder="Search movie" onChange={event => setQuery(event.target.value)} />
                <button>
                    <Link to="/blog"><img src={search} alt="search" onClick={() =>
                        setUrl(`https://api.themoviedb.org/3/search/movie?api_key=e2a2f53fe94c336a47e632ddb6b9fc26&language=en-US&query=${query}&page=all&include_adult=false`)
                    } /></Link>
                </button>
            </div>
        </searchContext.provider>
                <div className={styles.menu__modal}>
                    <button type="button" className="btn" data-mdb-toggle="modal" data-mdb-target="#menu">
                     Menu
                    </button>
                <div className="modal right fade" id="menu" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true" data-mdb-backdrop="false" data-mdb-keyboard="true">
                    <div className="modal-dialog modal-side modal-top-right ml-0">
                        <div className="modal-content">
                            <button type="button" className="btn-close" data-mdb-dismiss="modal" aria-label="Close"></button>
                                <Sidebar/>
                            <button type="button" className="btn btn-secondary" data-mdb-dismiss="modal">
                              Close
                            </button>
                          </div>
                        </div>
                    </div>   
                </div>
            <span>Sign Up</span>

            </div>

        </div>
    )
}
